    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed

        
        System.out.println("Ingrese el precio del producto:");
        double precio = Float.parseFloat(txtPrecioProducto.getText().trim());

        System.out.println("Ingrese la cantidad de unidades adquiridas:");
        int cantidad = (int) Float.parseFloat(txtCantidadUnidades.getText().trim());

        // Calcular el importe de la compra
        double importeCompra = precio * cantidad;

        // Calcular el primer descuento
        double descuento1 = 0.07 * importeCompra;

        // Calcular el importe restante despu√©s del primer descuento
        double importeRestante = importeCompra - descuento1;

        // Calcular el segundo descuento
        double descuento2 = 0.07 * importeRestante;

        // Calcular el descuento total
        double descuentoTotal = descuento1 + descuento2;

        // Calcular el importe a pagar
        double importePagar = importeCompra - descuentoTotal;
        
        txtImporte.setText(String.valueOf(importeCompra));
        txtprimerdescuento.setText(String.valueOf(descuento1));
        txtsegundodescuento.setText(String.valueOf(descuento2));
        txttotaldescuento.setText(String.valueOf(descuentoTotal));
        txtimportepagar.setText(String.valueOf(importePagar));
    }//GEN-LAST:event_jButton1ActionPerformed

